import React,{useState, useEffect}from "react";
import Editor from "./Editor";


function App() {

  const[html,setHtml]=useState('')
  const[css,setCss]=useState('')
  const[js,setJs]=useState('')

  const srcDoc=`
    <html>
      <body>${html}</body>
      <style>${css}</style>
      <script>${js}</script>
    </html
  `
  useEffect(()=>{
    const timeout=setTimeout(()=>{
      setSrcDoc
    })
  })

  return (
    <div>
      <div className="pane top-pane">
        <Editor
          language="xml"
          displayName="HTML"
          value={html}
          onChange={setHtml}
        />
        <Editor
          language="css"
          displayName="CSS"
          value={css}
          onChange={setCss}
        />
        <Editor
          language="javascript"
          displayName="JS"
          value={js}
          onChange={setJs}
        />
      </div>
      <div>
        <iframe
          srcDoc={srcDoc}
          title="output"
          sandbox="allow-scripts"
          style={{border:0+"px"}}
          width="100%"
          height="100%"
        />
      </div>
    </div>
  )
}

export default App;
